package leetcode

import java.util.LinkedList
import java.util.Queue

class Solution934 {
    fun shortestBridge(grid: Array<IntArray>): Int {
        markSecondIsland(grid)
        print(grid)
        val q: Queue<Triple<Int, Int, Int>> = LinkedList()
        putFirstIslandToQueue(grid, q)

        return run(grid, q) - 1
//        return 0
    }

    fun print(grid: Array<IntArray>) {
        grid.forEach { row ->
            println(row.contentToString())
        }
    }

    private fun run(grid: Array<IntArray>, q: Queue<Triple<Int, Int, Int>>): Int {
        while (q.isNotEmpty()) {
            val c = q.remove()
            val i = c.first
            val j = c.second
            val r = c.third

            if (grid[i][j] == -1) {
                return r
            }

            if (grid[i][j] == 0) {
                grid[i][j] = r
            } else if (r > 0) {
                continue
            }

            if (i > 0 && grid[i - 1][j] <= 0) {
                q.add(Triple(i - 1, j, r + 1))
            }
            if (i < grid.size - 1 && grid[i + 1][j] <= 0) {
                q.add(Triple(i + 1, j, r + 1))
            }
            if (j > 0 && grid[i][j - 1] <= 0) {
                q.add(Triple(i, j - 1, r + 1))
            }
            if (j < grid[i].size - 1 && grid[i][j + 1] <= 0) {
                q.add(Triple(i, j + 1, r + 1))
            }
        }
        return -1
    }

    private fun putFirstIslandToQueue(grid: Array<IntArray>, q: Queue<Triple<Int, Int, Int>>) {
        for (i in grid.indices) {
            for (j in 0 until grid[i].size) {
                if (grid[i][j] == 1) {
                    q.add(Triple(i, j, 0))
                }
            }
        }
    }

    private fun markSecondIsland(grid: Array<IntArray>) {
        for (i in grid.indices) {
            for (j in 0 until grid[i].size) {
                if (grid[i][j] == 1) {
                    markSecondIslandRecur(grid, Pair(i, j))
                    return
                }
            }
        }
    }

    private fun markSecondIslandRecur(grid: Array<IntArray>, current: Pair<Int, Int>) {
        val i = current.first
        val j = current.second

        if (grid[i][j] == 1) {
            grid[i][j] = -1
        }

        if (i > 0 && grid[i - 1][j] == 1) {
            markSecondIslandRecur(grid, Pair(i - 1, j))
        }
        if (i < grid.size - 1 && grid[i + 1][j] == 1) {
            markSecondIslandRecur(grid, Pair(i + 1, j))
        }
        if (j > 0 && grid[i][j - 1] == 1) {
            markSecondIslandRecur(grid, Pair(i, j - 1))
        }
        if (j < grid[i].size - 1 && grid[i][j + 1] == 1) {
            markSecondIslandRecur(grid, Pair(i, j + 1))
        }
    }

    fun transformInput(str: String): Array<IntArray> {
        return str
            .removePrefix("[")
            .removeSuffix("]")
            .split("],")
            .map { a ->
                val aa = a.removePrefix("[").removeSuffix("]")
                if (aa.isEmpty()) {
                    emptyList<Int>().toIntArray()
                } else {
                    aa.split(",").map { it.toInt() }.toIntArray()
                }
            }
            .toTypedArray()
    }
}

